import React, { useState } from "react";

function CreateArea() {

  const [noteTitle,setNoteTitle] = useState({
   title:"",
   content:""
  });

  return (
    <div>
      <form>
        <input name="title" placeholder="Title" value={noteTitle.title} />
        <textarea name="content" placeholder="Take a note..." rows="3" value={noteTitle.content} />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
