import React from "react";

function CreateArea() {

  const [noteTitle,setNoteTitle] = React.useState({
   title:"",
   content:""
  });

  function noteInputs(event){
    const [name,value] = event.target;

    setNoteTitle(prevValue => {
      return{
        ...prevValue,
        [name] : value
      };
    });
  }


  return (
    <div>
      <form>
        <input name="title" onChange={noteInputs} placeholder="Title" value={noteTitle.title} />
        <textarea name="content" onChange={noteInputs} placeholder="Take a note..." rows="3" value={noteTitle.content} />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
